mapper.select:
SELECT title
FROM board
WHERE board_id = #{boardId}

SELECT tag
FROM board
WHERE board_id = #{boardId}

mapper.update:
UPDATE board
SET title = #{title},
content = #{content},
tag = #{tag}
WHERE board_id = #{boardId}

mapper.delete:
UPDATE board
SET is_deleted = TRUE
WHERE board_id = #{boardId}
