mapper.update:
UPDATE  crew
SET     status = #{status}
WHERE   crew_id = #{crewId}

mapper.select:
SELECT  crew_id AS crewId,
        user_id AS userId,
        guild_id AS guildId,
        status
FROM crew
WHERE
    guild_id = #{guildId}

        AND user_id != #{userId}



            AND status = #{status}


            AND status = 0
