mapper.select:
SELECT board_id as boardId, user_id as userId, title, content, reg_date as regDate, view_cnt as viewCnt, tag
FROM board
WHERE board_id = #{boardId}

mapper.update:
UPDATE user
SET user_name = #{userName},
password = #{password},
email = #{email}
WHERE user_id = #{userId}

mapper.delete:
UPDATE user
SET is_deleted = true
WHERE user_id = #{userId}
